mapper.select:
SELECT cy.*, cl.CL001,cl.CL002,co.name,cg.cg001,cg.CG003,sa.SA019 uname, IFNULL(COUNT(*),0) rwcs, IFNULL(SUM(cy.CY006),0) gjlc, IFNULL(SUM(TIMESTAMPDIFF(HOUR,CY003,CY004)),0) rwsc FROM st4_scs_cy cy LEFT JOIN st4_sys_sa sa ON cy.sa001=sa.sa001 LEFT JOIN st4_scs_cg cg ON cg.CY017 = cy.CY017 LEFT JOIN st4_scs_cl cl ON cy.CL003 = cl.CL003 LEFT JOIN st4_scs_cc cc ON cc.CY017 = cy.CY017 LEFT JOIN st4_scs_ck ck ON ck.CC002 = cc.CC002 LEFT JOIN st4_po_cl_co clco ON clco.CL001 = cl.CL001 LEFT JOIN rlhd_group co ON co.id = clco.CO001 WHERE cy.cy016=1
and cl.CL002 LIKE '%${taskName}%'
and co.id = #{ledgerId}

select cy.*,sa.SA019,sg.SG008,sg.SG001,cg.*,cc.*,ce.*,cf.* from st4_scs_cy cy left join st4_sys_sa sa on cy.SA001 = sa.SA001 left join st4_sys_sg sg on sg.SG001 = cy.CY010 LEFT JOIN st4_scs_cg cg ON cg.CY017 = CY.CY017 LEFT JOIN st4_scs_cc cc ON cc.`CY017` = cy.CY017 left join st4_scs_ce ce on ce.CE002 = cc.CC002 left join st4_scs_cf cf on cf.CC002 = cc.CC002
where cy.CY016=1
and cy.CY001 = #{cy001}

select * from (
select
sc.sc002,
,
TIMESTAMPDIFF(HOUR,CY003,CY004) cy007,
sa.SA019,sg.SG008
from st4_scs_cy cy

left join st4_sys_sa sa on cy.SA001 = sa.SA001
left join st4_sys_sg sg on sg.SG001 = cy.CY010

left join st4_scs_cl cl on cy.CL003=cl.CL003
left join st4_sys_sc sc on cy.cy011=sc.sc001
where cy.CY016=1
and (sc.sc018 is null or (sc.sc018 = 2  ) )


    and cy.SA001 = #{cy.sa001} OR sa.SA002=#{cy.sa001}


    and cy.SA001 = #{cy.sa001}


    and sa.SA019 like '%${cy.xcry}%'


    and cy.CY010 = #{cy010}


    and cl.CL001 = #{cy.cl001}


    and CY003  >=  #{strTime}


    and CY004  <=  #{cy.endTime}

order by cy.CY013 desc

    limit #{cy.pageNumber},#{cy.pageSize}


) cy
left join st4_scs_cg cg on cy.CY017=cg.CY017
order by cy.CY013 desc
