mapper.select:
select distinct sg.*,sd.SD008,ca.CA002,cb.CB002
from st4_sys_sg sg
 LEFT JOIN st4_po_sg_sj sgsj
 ON sgsj.SG001 = sg.SG001
 LEFT JOIN st4_sys_sj sj
 ON sj.SJ001 = sgsj.SJ001
 LEFT JOIN sys_company sd
 ON sd.PK_ID = sg.SD001
 LEFT JOIN st4_scs_ca ca
 ON ca.CA001 = sg.CA001
 LEFT JOIN st4_scs_cb cb
 ON cb.CB001 = sg.CB001
WHERE sg.SG007=1

     and sj.SJ001 = #{sg.unitId}


     and  SG002 = #{sg.sg002}


     and  SG003 = #{sg.sg003}


     and  SG004 = #{sg.sg004}


     and  SG005 = #{sg.sg005}


     and SG006 = #{sg.sg006}


     SG007 = #{sg.sg007}


     and  SG008 like '%${sg.sg008}%'


     and  SG009 = #{sg.sg009}


     and sg.SD001 = #{sg.sd001}


     and  SG011 = #{sg.sg011}


     and  sg.CA001 = #{sg.ca001}


     and  sg.CB001 = #{sg.cb001}


     and  SG013 = #{sg.sg013}


     and  SG014 = #{sg.sg014}


     and  SG015 = #{sg.sg015}


     and  SG016 = #{sg.sg016}


     and  SG017 = #{sg.sg017}


     and  SG018 = #{sg.sg018}


     and  SG019 = #{sg.sg019}


 ORDER BY sg.SG004 desc

mapper.update:
update st4_sys_sg
set SG007 = 0
where SG001 = #{sg001,jdbcType=INTEGER}

mapper.select:
select SG001,SG008 from st4_sys_sg where SG007 = 1

    and SG008 = #{sg008}


    and SD001 = #{sd001}

ORDER BY CONVERT(SG008 USING gbk) COLLATE gbk_chinese_ci ASC

SELECT  a.*,b.CS003 AS CS003,b.CS004,b.CS005,
    b.CS006,b.CS011
FROM st4_sys_sg a
LEFT JOIN (
        SELECT d.*
        FROM (
        SELECT MAX(cs.CS001) AS CS001
        FROM st4_scs_cs cs
        GROUP BY SG001
        ) t
        INNER JOIN st4_scs_cs d ON t.CS001 = d.CS001
) b
ON a.SG001 = b.SG001
    WHERE a.SG007 = 1

        and a.SG001 = #{sg001}


        and a.SG001 IN

            #{item}



        and a.SG003  >=  #{strTime}


        and a.SG003  <=  #{endTime}

    ORDER BY b.CS003 DESC

select a.*,Max(b.CS003) as CS003,b.CS004,CS005,
b.CS006,c.`SD008`,c.`SD001`,c.`SD012` , d.`SM002`,d.SM001,
e.`CB002`, e.`CB001`  from  ( select
sg.SG001, SG002, SG003, SG004, SG005, SG006, SG007, SG008, SG009, SD001, SG011, CA001,
CB001, SG013, SG014, SG015, SG016, SG017, SG018, SG019
from st4_sys_sg sg
LEFT JOIN st4_po_sa_sg sasg ON sasg.`SG001` = sg.`SG001`
WHERE SG007 = 1

    and  SA001 = #{unionId}


    and  SG002 = #{sg002}


    and  SG003 = #{sg003}


    and  SG004 = #{sg004}


    and  SG005 = #{sg005}


    and SG006 = #{sg006}


    AND SG007 = #{sg007}


    and  SG008 like '%${sg008}%'


    and  SG009 = #{sg009}


    and SD001 = #{sd001}


    and  SG011 = #{sg011}


    and  CA001 = #{ca001}


    and  CB001 = #{cb001}


    and  SG013 = #{sg013,}


    and  SG014 = #{sg014}


    and  SG015 = #{sg015}


    and  SG016 = #{sg016}


    and  SG017 = #{sg017}


    and  SG018 = #{sg018}


    and  SG019 = #{sg019}

 ) a
left join st4_scs_cs b on a.SG001 = b.SG001
LEFT JOIN st4_sys_sn e ON e.`SN001` = a.CB001
left join st4_sys_sm d ON a.SM001 = d.`CA001`
LEFT JOIN sys_company c ON a.SD001 = c.PK_ID
where a.SG007 = 1
GROUP BY SG001 ORDER BY CONVERT(SG008 USING gbk) COLLATE gbk_chinese_ci ASC

select SG001, SG008 from st4_sys_sg sg WHERE SG007 = 1
and SG008 like '%${sg008}%'
ORDER BY CONVERT(SG008 USING gbk) COLLATE gbk_chinese_ci ASC limit #{pageNumber},#{pageSize}
