    /**
     * 获取图片宽度
     * @param file  图片文件
     * @return 宽度
     */
    public static int getImgWidth(File file) {
        InputStream is = null;
        BufferedImage src = null;
        int ret = -1;
        try {
            is = new FileInputStream(file);
            src = ImageIO.read(is);
            ret = src.getWidth(null); // 得到源图宽
            is.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
        return ret;
    }


    /**
     * 获取图片高度
     * @param file  图片文件
     * @return 高度
     */
    public static int getImgHeight(File file) {
        InputStream is = null;
        BufferedImage src = null;
        int ret = -1;
        try {
            is = new FileInputStream(file);
            src = ImageIO.read(is);
            ret = src.getHeight(null); // 得到源图高
            is.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
        return ret;
    }



    /**
     * 图片压缩
     * @param oldPath  原始图片全路径   D:/xxx/xxx.jpg
     * @param resize    缩小倍数       0.5
     * @param path      生成路径    D:/xxx/xxx
     * @param fileName    生成图片名称   xxx.jpg
     * @return
     */
    public static boolean zoomImage(String oldPath, Float resize, String path, String fileName){
        try {
            BufferedImage im = ImageIO.read(new FileInputStream(oldPath));
            /*原始图像的宽度和高度*/
            int width = im.getWidth();
            int height = im.getHeight();
            /*调整后的图片的宽度和高度*/
            int toWidth = (int) (Float.parseFloat(String.valueOf(width)) * resize);
            int toHeight = (int) (Float.parseFloat(String.valueOf(height)) * resize);
            /*新生成结果图片*/
            BufferedImage result = new BufferedImage(toWidth, toHeight, BufferedImage.TYPE_INT_RGB);
            result.getGraphics().drawImage(im.getScaledInstance(toWidth, toHeight, java.awt.Image.SCALE_SMOOTH), 0, 0, null);
            File ml = new File(path);
            if(!ml.isDirectory()) new File(path).mkdirs();
            File f = new File(path + fileName);
            String fileType = fileName.split("\\.")[fileName.split("\\.").length - 1];
            if (fileType == null) return false;
            ImageIO.write(result, fileType, f);
            result.flush();
            return true;
        } catch (IOException e) {
            return false;
        }
    }





    /**
     * 图片转向
     * @param degree  向右旋转角度
     * @param openUrl  原图路径
     * @param saveUrl  新图保存路径
     * @param saveName  新图名称
     * @param suffix    图片类型 只支持gif,jpg,png
     * @return
     * @throws Exception
     */
    public static boolean turn(int degree,String openUrl,String saveUrl,String saveName,String suffix) throws Exception {
        int swidth = 0; // 旋转后的宽度
        int sheight = 0; // 旋转后的高度
        int x; // 原点横坐标
        int y; // 原点纵坐标
        File file = new File(openUrl);
        BufferedImage bi = ImageIO.read(file); // 读取该图片
        // 处理角度--确定旋转弧度
        degree = degree % 360;
        if (degree < 0)
            degree = 360 + degree;// 将角度转换到0-360度之间
        double theta = Math.toRadians(degree);// 将角度转为弧度
        // 确定旋转后的宽和高
        if (degree == 180 || degree == 0 || degree == 360) {
            swidth = bi.getWidth();
            sheight = bi.getHeight();
        } else if (degree == 90 || degree == 270) {
            sheight = bi.getWidth();
            swidth = bi.getHeight();
        } else {
            swidth = (int) (Math.sqrt(bi.getWidth() * bi.getWidth() + bi.getHeight() * bi.getHeight()));
            sheight = (int) (Math.sqrt(bi.getWidth() * bi.getWidth() + bi.getHeight() * bi.getHeight()));
        }
        x = (swidth / 2) - (bi.getWidth() / 2);// 确定原点坐标
        y = (sheight / 2) - (bi.getHeight() / 2);
        BufferedImage spinImage = new BufferedImage(swidth, sheight, bi.getType());
        // 设置图片背景颜色
        Graphics2D gs = (Graphics2D) spinImage.getGraphics();
        gs.setColor(Color.white);
        gs.fillRect(0, 0, swidth, sheight);// 以给定颜色绘制旋转后图片的背景
        AffineTransform at = new AffineTransform();
        at.rotate(theta, swidth / 2, sheight / 2);// 旋转图象
        at.translate(x, y);
        AffineTransformOp op = new AffineTransformOp(at,AffineTransformOp.TYPE_BICUBIC);
        spinImage = op.filter(bi, spinImage);
        File sf = new File(saveUrl, saveName);
        return ImageIO.write(spinImage, suffix, sf); // 保存图片
    }


